import './Profile.css';

import { Link } from 'react-router-dom';
import{ useState,useEffect } from 'react';
import { getUserInfo } from "../api/UserApi";

export const Profile = () => {

  const [user, setUser]=useState(undefined);
  const [update, setUpdate]=useState(false);
  useEffect(()=>{
      getUserInfo().then(x => setUser(x))
  },[ ]);

  if(!user){
     return<>Error</>
  }

  const handlePhoto = (e) => {
    setPhoto(e.target.value);
    setUpdate(false);
  };   

  const handleUpdate = (e) => {
    e.preventDefault();
      setUpdate(true);
      //setError(true);
      //createAccountStudent(userName, password, firstName, lastName, ID);  
};

    return(<div className="profileContainer">
        <div className="textContainer">
            <img src={"logo1(6).png"} className="logo"/>
            <h1>Hello!! {user[0].username}</h1>
            <h5>FirstName: {user[0].firstname} </h5>
            <h5>LastName: {user[0].lastname}</h5>
            <h5>Email: {user[0].email} </h5>
            <h5>SMU ID: {user[0].student_id}</h5>
            <h5>Photo: </h5>
            <div className="Row">
            <div className="Column">
                {(() => {
                if(user[0].profile_pic===null){
                    return <img src='https://via.placeholder.com/500' className="profilePic"/>
                }
                else{
                    return <img src={user[0].profile_pic}/>
                }
                })()}
            </div>
            <div className="Column">
                <input className="updatePhoto" onChange={handlePhoto}></input>
                <button>Update image</button>
            </div>
            </div>
            <h5>New Password: </h5>
            <br/>
            <br/>
            <br/>
            <button>Update</button>
            <button><Link to='/studentHome'>Back</Link></button>
        </div> 
    </div>
    );
};
