import './Profile.css';

import { Link } from 'react-router-dom';
import{ useState,useEffect } from 'react';
import { getUserInfo } from "../api/UserApi";

export const Profile = () => {

  const [user, setUser]=useState(undefined);
  const [emptyPhoto,setEmptyPhoto]=useState(false);
  const photolink="https://via.placeholder.com/500";

  useEffect(()=>{
      getUserInfo().then(x => setUser(x))
  },[ user ]);

  if(!user){
     return<>Loading..</>
  }

  const handlePhoto = () => {
    if(user[0].profile_pic===null){
        setEmptyPhoto(true);
        console
    }

  };

    return(<div className="profileContainer">
        <div className="textContainer">
            <img src={"logo1(6).png"} className="logo"/>
            <h1>Hello!! {user[0].username}</h1>
            <h5>FirstName: {user[0].firstname} </h5>
            <h5>LastName: {user[0].lastname}</h5>
            <h5>Email: {user[0].email} </h5>
            <h5>SMU ID: {user[0].student_id}</h5>
            <h5>Photo: </h5>
            <div className="portrait" style={{display: emptyPhoto ? '':'none'}}>
                <image src="https://via.placeholder.com/500"></image>
            </div>
            <div className="portrait" style={{display: emptyPhoto ? 'none' : ''}}>
                <image src={user[0].profile_pic}></image>
            </div>

            <h5>New Password: </h5>

            <button>Update</button>
            <button><Link to='/studentHome'>Back</Link></button>

        </div>


    </div>

    );
}
